<script type="text/javascript">
function cari(){
	$('#loading').html("<img src='https://www.insancargodepok.com/assets/img/loading.gif' />");
	var loading = $("#loading");
	var selectValues = $("#tujuan").val();
	if (selectValues == 0){
		var msg = "Tujuan tidak ditemukan";
		$('#info').html(msg);
	}else{
		var kode = {asal:$("#asal").val(),tujuan:$("#tujuan").val()};
		// $('#kab').attr("disabled",true);
		$.ajax({
				type: "POST",
				url : "https://www.insancargodepok.com/cadmin/home/lacak_tujuan",
				data: kode,
				beforeSend: function(){
				   // $("#loaderDiv").show();
				   loading.fadeIn();						
				},
				success: function(msg){
					$('#info').html(msg);
					loading.fadeOut();
				}
		});
	}
}
</script>
																				
